import React, { Component } from 'react'
import ModStudent from './ModStudent'
import { Query } from 'react-apollo'
import gql from 'graphql-tag'


export const FEED_QUERY = gql`
    {
        allStudents_sort {
          id
          student_rut
          list_number
          present
        }
      }
    `
class ModStudentList extends Component {
  _updateCacheAfterVote = (store, createVote, linkId) => {
    const data = store.readQuery({ query: FEED_QUERY })

    const votedLink = data.feed.links.find(link => link.id === linkId)
    votedLink.votes = createVote.link.votes

    store.writeQuery({ query: FEED_QUERY, data })
  }


    render() {
        return (
          <Query query={FEED_QUERY}>
            {({ loading, error, data }) => {
              if (loading) return <div>Fetching</div>
              if (error) return <div>Error</div>

              const studentsToRender = data.allStudents_sort

              return (
                <div>
                  {studentsToRender.map(student => <ModStudent key={student.id} student={student} updateStoreAfterVote={this._updateCacheAfterVote}/>)}
                </div>
              )
            }}
          </Query>
        )
      }
}

export default ModStudentList
