import React, { Component } from 'react'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'

const POST_MUTATION = gql`
    mutation PostMutation($student_rut: String!, $list_number: String!){
        createStudent(student_rut: $student_rut, list_number: $list_number) {
        id
        student_rut
        list_number
        present
        }
  }
`


class CreateStudent extends Component {
  state = {
    student_rut: '',
    list_number: '',
  }

  render() {
    const { student_rut, list_number } = this.state
    return (
      <div>
        <div className="flex flex-column mt3">
          <input
            className="mb2"
            value={student_rut}
            onChange={e => this.setState({ student_rut: e.target.value })}
            type="text"
            placeholder="Rut del estudiante"
          />
          <input
            className="mb2"
            value={list_number}
            onChange={e => this.setState({ list_number: e.target.value })}
            type="text"
            placeholder="The URL for the link"
          />
        </div>

        <Mutation mutation={POST_MUTATION}
         variables={{ student_rut, list_number }}
         onCompleted={() => this.props.history.push('/')}
         update={(store, { data: { post } }) => {
            const data = store.readQuery({ query: FEED_QUERY })
            data.feed.links.unshift(post)
            store.writeQuery({
                query: FEED_QUERY,
               data
              })
            }}
         >
            {postMutation => <button onClick={postMutation}>Guardar</button>}
        </Mutation>
      </div>
    )
  }
}

export default CreateStudent
