import React, { Component } from 'react'
//import ModStudent from './ModStudent'
import { Query } from 'react-apollo'
import gql from 'graphql-tag'
import { Mutation } from 'react-apollo'

export const FEED_QUERY = gql`
  query{
        allStudents_sort {
          id
          student_rut
          list_number
          present
        }
      }
    `

    const UPDATE_RUT_MUTATION = gql`
    mutation UpdateMutation($student_rut: String!){
        update_rut(student_rut: $student_rut) {
          id
          student_rut
          list_number
        }
      }
`

const DELETE_MUTATION = gql`
    mutation DeleteMutation($student_rut: String!){
        delete(student_rut: $student_rut) {
          id
          student_rut
          list_number
        }
      }
`

class ModStudentList extends Component {



    render() {
        return (
          <Query query={FEED_QUERY}>
            {({ loading, error, data }) => {
              if (loading) return <div>Fetching</div>
              if (error) return <div>Error</div>

              const studentsToRender = data.allStudents_sort

              return (
                <div>
                  {studentsToRender.map(student => <ModStudent key={student.id} student={student} />)}
                </div>
              )
            }}
          </Query>
        )
      }
}

export default ModStudentList
