import React, { Component } from 'react'
import { timeDifferenceForDate } from '../utils'
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'


const PRESENT_MUTATION = gql`
  mutation PresentMutation($studentId: ID!, $presentValue: String!) {
    is_present(id: $studentId, present: $presentValue) {
      id
      student_rut
      list_number
      present
    }
  }
`

class Student extends Component {
  render() {
    return (
      <div className="flex mt2 items-start">
      <div className="flex items-center">
        <span className="gray">{this.props.student.list_number}.</span>

          <Mutation
           mutation={PRESENT_MUTATION}
            variables={{ studentId: this.props.student.id, presentValue: '' }}
            >
            {presentMutation => (
              <div className="ml1 gray f11" onClick={presentMutation}>
                <input type="checkbox" defaultChecked={this.state.chkbox} onChange={this.handleChangeChk} />

              </div>
            )}
          </Mutation>

      </div>
      <div className="ml1">
        <div>
          {this.props.student.student_rut}  ()

        </div>
        <div className="f6 lh-copy gray">
          {this.props.student.present} votes | by{' '}

        </div>
      </div>
    </div>
    )
  }
}
export default Student
